select

from exam_examination

     and exam_examination.id = #{ination.id}
     and exam_examination.dept_id = #{ination.deptId}
     and exam_examination.exam_paper_id = #{ination.examPaperId}
     and exam_examination.train_course_id = #{ination.trainCourseId}
     and exam_examination.name = #{ination.name}
     and exam_examination.type = #{ination.type}
     and exam_examination.enable_control_time = #{ination.enableControlTime}
     and exam_examination.start_time = #{ination.startTime}
     and exam_examination.end_time = #{ination.endTime}
     and exam_examination.time_length = #{ination.timeLength}
     and exam_examination.exam_number = #{ination.examNumber}
     and exam_examination.pass_mark = #{ination.passMark}
     and exam_examination.question_disorder = #{ination.questionDisorder}
     and exam_examination.finished_paper = #{ination.finishedPaper}
     and exam_examination.exam_end = #{ination.examEnd}
     and exam_examination.examination_user_limit = #{ination.examinationUserLimit}
     and exam_examination.create_by = #{ination.createBy}
     and exam_examination.create_date = #{ination.createDate}
     and exam_examination.update_by = #{ination.updateBy}
     and exam_examination.update_date = #{ination.updateDate}
     and exam_examination.remarks = #{ination.remarks}
     and exam_examination.del_flag = #{ination.delFlag}

select
DISTINCT 
,tc.name as traincourse_name,eeu.vip_user_id examExaminationUserVipUserId
from exam_examination exam_examination
INNER JOIN train_course tc ON exam_examination.train_course_id = tc.id
LEFT JOIN exam_examination_user eeu ON eeu.exam_examination_id = exam_examination.id
LEFT join train_course_user tcu on tcu.train_course_id=exam_examination.train_course_id

    ((eeu.vip_user_id = #{userId} and exam_examination.type = '2')
    or exam_examination.type = '1')
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')
     and exam_examination.id = #{ination.id}
     and exam_examination.dept_id = #{ination.deptId}
     and exam_examination.exam_paper_id = #{ination.examPaperId}
     and exam_examination.name = #{ination.name}
     and exam_examination.type = #{ination.type}
     and exam_examination.enable_control_time = #{ination.enableControlTime}
     and exam_examination.start_time = #{ination.startTime}
     and exam_examination.end_time = #{ination.endTime}
     and exam_examination.time_length = #{ination.timeLength}
     and exam_examination.exam_number = #{ination.examNumber}
     and exam_examination.pass_mark = #{ination.passMark}
     and exam_examination.question_disorder = #{ination.questionDisorder}
     and exam_examination.finished_paper = #{ination.finishedPaper}
     and exam_examination.exam_end = #{ination.examEnd}
     and exam_examination.examination_user_limit = #{ination.examinationUserLimit}
     and exam_examination.create_by = #{ination.createBy}
     and exam_examination.create_date = #{ination.createDate}
     and exam_examination.update_by = #{ination.updateBy}
     and exam_examination.update_date = #{ination.updateDate}
     and exam_examination.remarks = #{ination.remarks}
     and exam_examination.del_flag = #{ination.delFlag}

select
,tc.name as traincourse_name
from exam_examination exam_examination
INNER JOIN train_course tc ON exam_examination.train_course_id = tc.id
left JOIN vip_user_orders vuo ON tc.id = vuo.train_course_id

    IF
    (
    tc.price > 0,
    vuo.vip_user_id = #{userId} and
    vuo.del_flag =1 and
    exam_examination.type = '2' and
    exam_examination.id not in (select exam_examination_id from exam_examination_user where vip_user_id = #{userId})
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')  ,
    exam_examination.type = '2' and
    exam_examination.id not in (select exam_examination_id from exam_examination_user where vip_user_id = #{userId})
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')  
    )
     and exam_examination.id = #{ination.id}
     and exam_examination.dept_id = #{ination.deptId}
     and exam_examination.exam_paper_id = #{ination.examPaperId}
     and exam_examination.name = #{ination.name}
     and exam_examination.type = #{ination.type}
     and exam_examination.enable_control_time = #{ination.enableControlTime}
     and exam_examination.start_time = #{ination.startTime}
     and exam_examination.end_time = #{ination.endTime}
     and exam_examination.time_length = #{ination.timeLength}
     and exam_examination.exam_number = #{ination.examNumber}
     and exam_examination.pass_mark = #{ination.passMark}
     and exam_examination.question_disorder = #{ination.questionDisorder}
     and exam_examination.finished_paper = #{ination.finishedPaper}
     and exam_examination.exam_end = #{ination.examEnd}
     and exam_examination.examination_user_limit = #{ination.examinationUserLimit}
     and exam_examination.create_by = #{ination.createBy}
     and exam_examination.create_date = #{ination.createDate}
     and exam_examination.update_by = #{ination.updateBy}
     and exam_examination.update_date = #{ination.updateDate}
     and exam_examination.remarks = #{ination.remarks}
     and exam_examination.del_flag = #{ination.delFlag}
