mapper.select:
select

from exam_examination

     and exam_examination.id = #{id}
     and exam_examination.dept_id = #{deptId}
     and exam_examination.exam_paper_id = #{examPaperId}
     and exam_examination.train_course_id = #{trainCourseId}
     and exam_examination.name like concat('%', #{name}, '%')
     and exam_examination.type = #{type}
     and exam_examination.enable_control_time = #{enableControlTime}
     and exam_examination.start_time = #{startTime}
     and exam_examination.end_time = #{endTime}
     and exam_examination.time_length = #{timeLength}
     and exam_examination.exam_number = #{examNumber}
     and exam_examination.pass_mark = #{passMark}
     and exam_examination.question_disorder = #{questionDisorder}
     and exam_examination.finished_paper = #{finishedPaper}
     and exam_examination.exam_end = #{examEnd}
     and exam_examination.examination_user_limit = #{examinationUserLimit}
     and exam_examination.create_by = #{createBy}
     and exam_examination.create_date = #{createDate}
     and exam_examination.update_by = #{updateBy}
     and exam_examination.update_date = #{updateDate}
     and exam_examination.remarks = #{remarks}
     and exam_examination.del_flag = #{delFlag}

order by exam_examination.update_date desc

select
DISTINCT 
,tc.name as traincourse_name
,eeu.vip_user_id examExaminationUserVipUserId
,eeu.create_date examExaminationCreateDate
from exam_examination exam_examination
INNER JOIN train_course tc ON exam_examination.train_course_id = tc.id
LEFT JOIN exam_examination_user eeu ON eeu.exam_examination_id = exam_examination.id
LEFT join train_course_user tcu on tcu.train_course_id=exam_examination.train_course_id

    ((eeu.vip_user_id = #{userId} and exam_examination.type = '2')
    or exam_examination.type = '1')
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')
     and exam_examination.id = #{id}
     and exam_examination.dept_id = #{deptId}
     and exam_examination.exam_paper_id = #{examPaperId}
     and exam_examination.name like concat('%', #{name}, '%')
     and exam_examination.type = #{type}
     and exam_examination.enable_control_time = #{enableControlTime}
     and exam_examination.start_time = #{startTime}
     and exam_examination.end_time = #{endTime}
     and exam_examination.time_length = #{timeLength}
     and exam_examination.exam_number = #{examNumber}
     and exam_examination.pass_mark = #{passMark}
     and exam_examination.question_disorder = #{questionDisorder}
     and exam_examination.finished_paper = #{finishedPaper}
     and exam_examination.exam_end = #{examEnd}
     and exam_examination.examination_user_limit = #{examinationUserLimit}
     and exam_examination.create_by = #{createBy}
     and exam_examination.create_date = #{createDate}
     and exam_examination.update_by = #{updateBy}
     and exam_examination.update_date = #{updateDate}
     and exam_examination.remarks = #{remarks}
     and exam_examination.del_flag = #{delFlag}

    ORDER BY eeu.create_date desc,exam_examination.create_date desc

select
,tc.name as traincourse_name
from exam_examination exam_examination
INNER JOIN train_course tc ON exam_examination.train_course_id = tc.id
left JOIN vip_user_orders vuo ON tc.id = vuo.train_course_id

    IF
    (
    tc.price > 0,
    vuo.vip_user_id = #{userId} and
    vuo.del_flag =1 and
    exam_examination.type = '2' and
    exam_examination.id not in (select exam_examination_id from exam_examination_user where vip_user_id = #{userId})
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')  ,
    exam_examination.type = '2' and
    exam_examination.id not in (select exam_examination_id from exam_examination_user where vip_user_id = #{userId})
    and
    ((enable_control_time = '1' and end_time > NOW() and start_time < NOW())
    or enable_control_time = '0')  
    )
     and exam_examination.id = #{id}
     and exam_examination.dept_id = #{deptId}
     and exam_examination.exam_paper_id = #{examPaperId}
     and exam_examination.name like concat('%', #{name}, '%')
     and exam_examination.type = #{type}
     and exam_examination.enable_control_time = #{enableControlTime}
     and exam_examination.start_time = #{startTime}
     and exam_examination.end_time = #{endTime}
     and exam_examination.time_length = #{timeLength}
     and exam_examination.exam_number = #{examNumber}
     and exam_examination.pass_mark = #{passMark}
     and exam_examination.question_disorder = #{questionDisorder}
     and exam_examination.finished_paper = #{finishedPaper}
     and exam_examination.exam_end = #{examEnd}
     and exam_examination.examination_user_limit = #{examinationUserLimit}
     and exam_examination.create_by = #{createBy}
     and exam_examination.create_date = #{createDate}
     and exam_examination.update_by = #{updateBy}
     and exam_examination.update_date = #{updateDate}
     and exam_examination.remarks = #{remarks}
     and exam_examination.del_flag = #{delFlag}

    ORDER BY exam_examination.create_date desc

mapper.update:
update exam_examination
dept_id = #{deptId}, exam_paper_id = #{examPaperId}, train_course_id = #{trainCourseId}, name = #{name}, type = #{type}, enable_control_time = #{enableControlTime}, start_time = #{startTime}, end_time = #{endTime}, time_length = #{timeLength}, exam_number = #{examNumber}, pass_mark = #{passMark}, question_disorder = #{questionDisorder}, finished_paper = #{finishedPaper}, exam_end = #{examEnd}, examination_user_limit = #{examinationUserLimit}, update_by = #{updateBy}, remarks = #{remarks}, del_flag = #{delFlag}, update_date = sysdate()
where id = #{id}
