mapper.select:
select

from exam_practice

     and id = #{id}
      and dept_id = #{deptId}
      and name  like concat('%', #{name}, '%')
     and exam_practice.train_course_id = #{trainCourseId}
     and enable_control_time = #{enableControlTime}
      and start_time = #{startTime}
      and end_time = #{endTime}
      and practice_user_limit = #{practiceUserLimit}
      and create_by = #{createBy}
      and create_date = #{createDate}
      and update_by = #{updateBy}
      and update_date = #{updateDate}
      and remarks = #{remarks}
      and del_flag = #{delFlag}

select
,
tc.name train_course_name
from exam_practice exam_practice
INNER join train_course tc on tc.id =exam_practice.train_course_id
Left join vip_user_orders vuo ON exam_practice.train_course_id = vuo.train_course_id

    IF
    (
    tc.price > 0,
    vuo.vip_user_id = 1
    AND vuo.del_flag = 0
    AND (
    ( exam_practice.enable_control_time = '1' AND exam_practice.end_time > NOW( ) AND exam_practice.start_time < NOW( ) )
    OR exam_practice.enable_control_time = '0'
    ),
    ((exam_practice.enable_control_time = '1' AND exam_practice.end_time > NOW( ) AND exam_practice.start_time < NOW( ) )
    OR exam_practice.enable_control_time = '0')
    )
     and exam_practice.id = #{id}
     and exam_practice.dept_id = #{deptId}
     and exam_practice.train_course_id = #{trainCourseId}
     and exam_practice.name like concat('%', #{name}, '%') 
     and exam_practice.enable_control_time = #{enableControlTime}
     and exam_practice.start_time = #{startTime}
     and exam_practice.end_time = #{endTime}
     and exam_practice.practice_user_limit = #{practiceUserLimit}
     and exam_practice.create_by = #{createBy}
     and exam_practice.create_date = #{createDate}
     and exam_practice.update_by = #{updateBy}
     and exam_practice.update_date = #{updateDate}
     and exam_practice.remarks = #{remarks}
     and exam_practice.del_flag = #{delFlag}

mapper.update:
update exam_practice
dept_id = #{deptId}, train_course_id = #{trainCourseId}, name= #{name}, enable_control_time = #{enableControlTime}, start_time = #{startTime}, end_time = #{endTime}, practice_user_limit = #{practiceUserLimit}, update_by = #{updateBy}, remarks = #{remarks}, del_flag = #{delFlag}, update_date = sysdate()
where id = #{id}
