mapper.select:
select

from train_course


    left join train_course_user tcu on tcu.train_course_id =train_course.id



     and tcu.vip_user_id = #{userId}
     and train_course.id = #{id}
      and train_course.dept_id = #{deptId}

         and (train_course.train_course_category_id in
         (select id from train_course_category where FIND_IN_SET(#{trainCourseCategoryId},parent_ids))
         or train_course.train_course_category_id = #{trainCourseCategoryId} )

      and train_course.name like CONCAT('%', #{name},'%')
      and train_course.cover = #{cover}
      and train_course.description = #{description}
      and train_course.state = #{state}
      and train_course.del_flag = #{delFlag}
      and train_course.create_by = #{createBy}
      and train_course.create_time = #{createTime}
      and train_course.update_by = #{updateBy}
      and train_course.update_time = #{updateTime}
      and train_course.remark = #{remark}


ORDER BY train_course.update_time desc
